package buclesPrevios;

public class Ej00PRevio {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			//necesita principio y final 
		//  el valor va incrementando
		// la varioable se comenza siempre por 1
		for(int i = 0; i <5 ; i++) {
			System.out.println(i);
			
		}
		System.out.println("bucle 2");
		for (int i =5; i>0 ; i--) {
			System.out.println(i);
		}
		System.out.println("bucle 3 ");
		for(int i =0 ; i<10 ;i++) {
		System.out.print(i);
		}
		
		System.out.println("   ");
		System.out.println("bucle 4 ");
		for(int i =0 ; i>=10 ;i++) {
		System.out.print(i);
		}
		}
			

	}


